import { SmileOutlined, UserOutlined } from '@ant-design/icons';
import { Avatar, Button, Form, Input, InputNumber, Modal, Typography } from 'antd';
import React, { useEffect, useRef, useState } from 'react';
const layout = {
  labelCol: {
    span: 8,
